--  DDL for Trigger TRG_SALARY_AUTO_UPDATE
--------------------------------------------------------

  CREATE OR REPLACE TRIGGER "TRG_SALARY_AUTO_UPDATE" 
BEFORE INSERT OR UPDATE ON HR_EMPLOYEE_CONTRACT
FOR EACH ROW
DECLARE
    v_tax NUMBER(15,2);
    v_insurance NUMBER(15,2);
    v_deduction NUMBER(15,2);
    v_net_salary NUMBER(15,2);
    v_salary_payment_date NUMBER(2,0);
BEGIN
    -- ✅ 급여 지급일 설정 (계약에서 가져옴, 기본값 25일)
    v_salary_payment_date := NVL(:NEW.SALARY_PAYMENT_DATE, 25);

    -- ✅ 세금 및 보험료 공제 계산 (예제: 세금 10%, 보험료 9%)
    v_tax := NVL(:NEW.ANNUAL_SALARY / 12, 0) * 0.10;
    v_insurance := NVL(:NEW.ANNUAL_SALARY / 12, 0) * 0.09;
    v_deduction := v_tax + v_insurance;

    -- ✅ 실수령액 계산
    v_net_salary := (NVL(:NEW.ANNUAL_SALARY / 12, 0) + NVL(:NEW.BONUS, 0) + NVL(:NEW.ADDITIONAL_PAY, 0)) - v_deduction;

    -- ✅ 급여 정보 업데이트 또는 삽입
    MERGE INTO HR_SALARY S
    USING (SELECT :NEW.CONTRACT_NUM AS CONTRACT_NUM FROM DUAL) D
    ON (S.CONTRACT_NUM = D.CONTRACT_NUM)
    WHEN MATCHED THEN
        UPDATE SET 
            MONTHLY_SALARY = NVL(:NEW.ANNUAL_SALARY / 12, 0),
            BONUS = NVL(:NEW.BONUS, 0),
            ADDITIONAL_PAY = NVL(:NEW.ADDITIONAL_PAY, 0),
            DEDUCTION = v_deduction,
            NET_SALARY = v_net_salary,
            SALARY_PAYMENT_DATE = NVL(:NEW.salary_payment_date, 25), 
            UPDATE_DATE = SYSDATE
    WHEN NOT MATCHED THEN
        INSERT (SALARY_NUM, EMPLOYEE_NUM, CONTRACT_NUM, COMPANY_NUM, MONTHLY_SALARY, BONUS, ADDITIONAL_PAY, DEDUCTION, NET_SALARY, SALARY_PAYMENT_DATE, REGISTER_DATE)
        VALUES (
            HR_SALARY_SEQ.NEXTVAL, :NEW.EMPLOYEE_NUM, :NEW.CONTRACT_NUM, :NEW.COMPANY_NUM, 
            NVL(:NEW.ANNUAL_SALARY / 12, 0), NVL(:NEW.BONUS, 0), NVL(:NEW.ADDITIONAL_PAY, 0), v_deduction, v_net_salary, 
            v_salary_payment_date, -- ✅ NUMBER 타입 그대로 저장
            SYSDATE
        );
END;
/
ALTER TRIGGER "TRG_SALARY_AUTO_UPDATE" DISABLE;
