--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "BHF_GOODS_IN" (
    p_branchOfficeId varchar2,
    p_dueDate DATE,
    p_companyNum number,
    p_deliveryId varchar2,
    p_orderCode varchar2,
    p_files GOODSINFILEARRAY 
)
is
    v_orderseq varchar2(1000);
    v_returnseq varchar2(1000);
begin
    FOR i IN 1..p_files.COUNT LOOP
    -- 창고 상품 입고(창고에 있는 상품이면 업데이트 없는 상품이면 인서트)
    MERGE INTO bhf_warehouse b
    USING(select p_files(i).branch_office_id as branch_office_id,
                 p_files(i).goods_code as goods_code,
                 p_files(i).goods_name as goods_name,
                 p_files(i).option_code as option_code,
                 p_files(i).option_name as option_name,
                 p_files(i).goods_standard as goods_standard,
                 p_files(i).stock_quantity as stock_quantity,
                 p_files(i).company_num as company_num
            from dual)m
       ON (b.option_code = m.option_code and b.branch_office_id = m.branch_office_id)
     WHEN MATCHED THEN
          UPDATE
             SET b.stock_quantity = (b.stock_quantity + m.stock_quantity)
     WHEN NOT MATCHED THEN
          INSERT (b.warehouse_code, 
                  b.branch_office_id,   
                  b.goods_code, 
                  b.goods_name, 
                  b.option_code, 
                  b.option_name, 
                  b.goods_standard, 
                  b.stock_quantity, 
                  b.company_num)
          VALUES ('bhf_home' || WAREHOUSE_CODE_SEQ.nextval, 
                  m.branch_office_id, 
                  m.goods_code, 
                  m.goods_name, 
                  m.option_code, 
                  m.option_name, 
                  m.goods_standard, 
                  m.stock_quantity, 
                  m.company_num);

    insert into bhf_in_list ( in_list_num, 
                            goods_lot_num, 
                            option_barcode, 
                            in_quantity, 
                            goods_consumption_date, 
                            branch_office_id,
                            company_num,
                            in_each_quantity,
                            option_code)
    values( in_list_num_seq.nextval,
            p_files(i).goods_lot_num,
            p_files(i).option_barcode,
            p_files(i).in_quantity,
            p_files(i).goods_consumption_date,
            p_files(i).branch_office_id,
            p_files(i).company_num,
            p_files(i).stock_quantity,
            p_files(i).option_code);
    -- 요청수량보다 입고수량이 많을시 반품
    IF p_files(i).in_quantity > p_files(i).quantity THEN

        insert into bhf_returning ( returning_code, branch_office_id, return_remark, company_num)
        values('bhf_return' || returning_code_seq.nextval, p_branchOfficeId, '요청 수량보다 많이 들어와서 반품', p_companyNum);

        SELECT ('bhf_return' || returning_code_seq.currval) 
        INTO v_returnseq
        FROM DUAL;

        insert into bhf_returning_detail(returning_detail_code, 
                                        returning_code, 
                                        goods_code,
                                        goods_name,
                                        option_code,
                                        option_name,
                                        quantity,
                                        exchange_returning_choice,
                                        returning_reason,
                                        option_barcode,
                                        goods_lot_num)
        values('bhf_return_dtl' || returning_detail_code_seq.nextval, 
                v_returnseq, 
                p_files(i).goods_code, 
                p_files(i).goods_name, 
                p_files(i).option_code, 
                p_files(i).option_name,  
                p_files(i).return_num,
                '반품',
                '요청 수량보다 많이 들어와서 반품',
                p_files(i).option_barcode,
                p_files(i).goods_lot_num
                );

        -- 창고 재고 차감
        UPDATE bhf_warehouse
        SET stock_quantity = stock_quantity - p_files(i).return_num
        WHERE option_code = p_files(i).option_code;
    -- 요청수량 보다 입고수량이 적을시 재발주
    ELSIF p_files(i).in_quantity < p_files(i).quantity THEN

        insert into bhf_order(order_code, branch_office_id, due_date, remark, company_num)
        values('bhf_ord' || order_code_seq.nextval, p_branchOfficeId, p_dueDate, '요청 수량보다 적게 들어와서 재요청', p_companyNum);

        SELECT ('bhf_ord' || order_code_seq.currval) 
        INTO v_orderseq
        FROM DUAL;

        insert into bhf_order_detail(order_detail_code, 
                                     order_code, 
                                     goods_code,
                                     goods_name,
                                     option_code,
                                     option_name, 
                                     goods_standard, 
                                     quantity)
        values('bhf_ord_dtl' || order_detail_code_seq.nextval, 
                v_orderseq, 
                p_files(i).goods_code, 
                p_files(i).goods_name, 
                p_files(i).option_code, 
                p_files(i).option_name, 
                p_files(i).goods_standard, 
                p_files(i).reorder);
    END IF;

    end loop;
    -- 입고 완료되면 상태변경
    update bsn_delivery
    set delivery_status = 'DS03'
    where delivery_id = p_deliveryId;
    update bhf_order
    set PROGRESS_STATUS = '완료'
    where order_code = p_orderCode;
end;

