--------------------------------------------------------
--  DDL for Procedure BSN_DELIVERY_CONFIRM
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "BSN_DELIVERY_CONFIRM" (
    p_delivery_id         VARCHAR2
)
IS
    -- 컬렉션 타입 선언
    TYPE lot_record IS RECORD (
        delivery_lot_detail_num NUMBER,
        goods_lot_num           VARCHAR2(40),
        real_delivery_qnt       NUMBER
    );
    TYPE lot_table_type IS TABLE OF lot_record;
    lot_table lot_table_type;

    v_lot_detail_num  NUMBER;
    v_goods_lot_num   VARCHAR2(40);
    v_delivery_qnt    NUMBER;
    v_remaining_qnt   NUMBER;
    v_status_check    NUMBER := 1; -- 출고 가능 여부 체크
    v_order_id        VARCHAR2(50);
    v_order_name      VARCHAR2(100);
    v_order_code      varchar2(30);

BEGIN
    -- LOT 데이터 컬렉션에 저장
    SELECT bdld.delivery_lot_detail_num, 
           bdld.goods_lot_num, 
           bdld.delivery_qnt * ( REGEXP_SUBSTR(bdd.goods_standard, '\d+', 2, 1)/ REGEXP_SUBSTR(bdd.goods_standard, '\d+', 1, 1) ) AS real_delivery_qnt
    BULK COLLECT INTO lot_table -- 다중 행을 검색하여 하나의 배열에 저장
    FROM bsn_delivery_lot_detail bdld
    LEFT JOIN bsn_delivery_detail bdd ON bdld.delivery_detail_id = bdd.delivery_detail_id
    WHERE bdld.delivery_detail_id IN (
        SELECT delivery_detail_id 
        FROM bsn_delivery_detail 
        WHERE delivery_id = p_delivery_id
    );

    IF lot_table.COUNT = 0 THEN
        RAISE_APPLICATION_ERROR(-20006, '해당 배송 ID에 대한 LOT 데이터가 없습니다. 배송 ID: ' || p_delivery_id);
    END IF;

    -- 먼저 출고 가능 여부 확인
    FOR i IN lot_table.FIRST .. lot_table.LAST LOOP
        v_lot_detail_num := lot_table(i).delivery_lot_detail_num;
        v_goods_lot_num  := lot_table(i).goods_lot_num;
        v_delivery_qnt   := lot_table(i).real_delivery_qnt;

        -- 창고 재고 확인
        BEGIN
            SELECT warehousing_remaining_quantity
            INTO v_remaining_qnt
            FROM purchs_warehousing_body
            WHERE goods_lot_num = v_goods_lot_num
            FOR UPDATE;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20002, '창고 재고 데이터가 없습니다. LOT 번호: ' || v_goods_lot_num);
        END;

        -- 재고 부족 예외 처리
        IF v_remaining_qnt < v_delivery_qnt THEN
            v_status_check := 0; -- 출고 불가능 상태
            EXIT;
        END IF;
    END LOOP;

    -- 출고 가능하면 상태 변경
    IF v_status_check = 1 THEN
        -- 창고 재고 차감
        FOR i IN lot_table.FIRST .. lot_table.LAST LOOP
            UPDATE purchs_warehousing_body
            SET warehousing_remaining_quantity = warehousing_remaining_quantity - lot_table(i).real_delivery_qnt
            WHERE goods_lot_num = lot_table(i).goods_lot_num;
        END LOOP;
        
        --LOT 상세 상태 : 출고 확정으로 변경
        UPDATE bsn_delivery_lot_detail bdld
        SET bdld.decision_status = 1
        WHERE bdld.delivery_detail_id IN (
            SELECT bdd.delivery_detail_id
            FROM bsn_delivery_detail bdd
            JOIN bsn_delivery bd ON bdd.delivery_id = bd.delivery_id
            WHERE bd.delivery_id = p_delivery_id
        );
        
        --상세 에서 상태 변경( 1-> 2)
        UPDATE bsn_delivery_detail bdd
        SET    bdd.sufficiency_status = 2
        WHERE  bdd.delivery_id = p_delivery_id;

        -- ORDER_ID 가져오기
        BEGIN
            SELECT order_id
            INTO   v_order_id
            FROM   bsn_delivery
            WHERE  delivery_id = p_delivery_id;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20004, '배송 데이터가 없습니다. 배송 ID: ' || p_delivery_id);
        END;

        -- ORDER_NAME, ORDER_CODE 가져오기
        BEGIN
            SELECT order_name, NVL(order_code, 'UNKNOWN')
            INTO   v_order_name, v_order_code
            FROM   bsn_order
            WHERE  order_id = v_order_id;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20005, '주문 데이터가 없습니다. 주문 ID: ' || v_order_id);
        END;
        
        

        -- 출고 테이블 상태 변경
        UPDATE bsn_delivery
        SET delivery_status = 'DS02', delivery_date = SYSDATE
        WHERE delivery_id = p_delivery_id;

        -- 주문 테이블 상태 변경
        UPDATE bsn_order
        SET order_status = 'VS002', sending_date = SYSDATE
        WHERE order_id = v_order_id;
        
        -- 주문코드가 'unknown'이 아니면 발주 상태 변경
        IF v_order_code IS NOT NULL AND v_order_code != 'UNKNOWN' THEN
            UPDATE bhf_order
            SET progress_status = '출고'  -- 출고 상태로 변경
            WHERE order_code= v_order_code;
            COMMIT;
        END IF;

        -- CS 변경 이력 저장
        INSERT INTO bsn_cs_change_history (
            HISTORY_NUM,
            ORDER_ID,
            HISTORY_TYPE,
            REGISTER_DATE,
            HISTORY_CONTENTS
        ) VALUES (
            BSN_CS_CHANGE_HISTORY_SEQ.nextval,
            v_order_id,
            'OH02',
            SYSDATE,
