--------------------------------------------------------
--  DDL for Procedure BSN_ORDER_CANCEL
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "BSN_ORDER_CANCEL" (
    p_order_id               bsn_order.order_id%TYPE,
    p_order_cancel_reason    bhf_order.order_cancel_reason%TYPE
)
IS
   v_order_code        bhf_order.order_code%TYPE;
   v_delivery_id       bsn_delivery.delivery_id%TYPE;
   v_order_name        bsn_order.order_name%TYPE;
   v_branch_office_id  bsn_order.branch_office_id%TYPE;

   TYPE delivery_dtl_record IS RECORD (
        delivery_detail_id       NUMBER
   );
   TYPE delivery_dtl_table_type IS TABLE OF delivery_dtl_record;
   delivery_dtl_table delivery_dtl_table_type;
BEGIN
    -- 현재 주문의 order_code, ...  조회
    SELECT order_code, order_name, branch_office_id
    INTO v_order_code, v_order_name, v_branch_office_id
    FROM bsn_order
    WHERE order_id = p_order_id;


    


    --주문 테이블에서 상태 갱신 및 사유 기록
    UPDATE bsn_order
    SET    remark = p_order_cancel_reason, 
           order_status = 'VS003'
    WHERE  order_id = p_order_id;


    -- order_code가 NULL 또는 빈 문자열이 아닐 때만 실행(상태 취소로 변경)
    IF v_order_code IS NOT NULL THEN
    -- OR v_order_code <> '' THEN
        UPDATE bhf_order
        SET    order_cancel_reason = p_order_cancel_reason, 
               PROGRESS_STATUS = '취소'
        WHERE  order_code = v_order_code;
    END IF;
    
    -- 현재 주문의 출고ID  조회
    SELECT delivery_id
    INTO v_delivery_id
    FROM bsn_delivery
    WHERE order_id = p_order_id;
    
    -- 출고 ID가 존재할 시
    IF v_delivery_id IS NOT NULL THEN
    
    -- 출고 상세 데이터 가져오기 (BULK COLLECT INTO 사용)
    SELECT delivery_detail_id
    BULK COLLECT INTO delivery_dtl_table
    FROM bsn_delivery_detail
    WHERE delivery_id = v_delivery_id;

    --출고 테이블에서 상태 갱신 및 사유 기록
    UPDATE bsn_delivery
    SET    remark = p_order_cancel_reason, 
           delivery_status = 'DS04'
    WHERE  order_id = p_order_id
    AND    delivery_id = v_delivery_id;

    FOR i IN 1 .. delivery_dtl_table.COUNT LOOP
        DELETE 
        FROM  bsn_delivery_lot_detail
        WHERE delivery_detail_id = delivery_dtl_table(i).delivery_detail_id;

        UPDATE bsn_delivery_detail
        SET    delivery_total_qnt = 0, sufficiency_status = 3
        WHERE  delivery_detail_id = delivery_dtl_table(i).delivery_detail_id;
    END LOOP;
    
    END IF;

    INSERT INTO bsn_cs_change_history (
        history_num, 
        order_id, 
        history_type, 
        history_contents
    )
    VALUES (
        bsn_cs_change_history_seq.nextval,  -- 이력 번호 시퀀스
        p_order_id, 
        'OH03',  -- 주문  취소 이력
        '지점ID: ' || v_branch_office_id || ', 주문 ID: ' || p_order_id || ', 주문명: ' || v_order_name || ' [주문 취소됨]' 
        || CHR(10) || '취소 사유: ' || p_order_cancel_reason
    );


EXCEPTION
    WHEN OTHERS THEN
        -- 오류 발생 시 롤백 및 오류 메시지 출력
        ROLLBACK;
        DBMS_OUTPUT.PUT_LINE('Error: ' || SQLERRM);
        RAISE;
END;

