    IF v_detail_count > 0 THEN
        v_order_name := v_first_goods_name || ' 외 ' || v_detail_count || '건';
    ELSE
        v_order_name := v_first_goods_name;
    END IF;

    -- 1. 주문 정보 저장
    INSERT INTO bsn_order (order_id, 
                           order_code, 
                           order_name,
                           branch_office_id,
                           order_date,
                           due_date, 
                           employee_num,
                           company_num,
                           remark)
    VALUES ('bsn_order' || bsn_order_seq.nextval, 
            p_order_code, 
            v_order_name,
            p_branch_office_id,
            p_order_date,
            p_due_date, 
            p_employee_num,
            p_company_num,
            p_remark);

    -- 2. 생성된 주문 ID 가져오기
    SELECT order_id
    INTO v_order_id
    FROM bsn_order
    WHERE order_code = p_order_code;

    -- 3. 배열을 상세 정보에 저장
    FOR i IN 1..p_files.COUNT LOOP
        INSERT INTO bsn_order_detail (order_detail_id, 
                                      order_id, 
                                      goods_code,
                                      option_code,
                                      goods_name,
                                      option_name,
                                      goods_standard, 
                                      quantity)
        VALUES (bsn_order_detail_seq.nextval, 
                v_order_id, 
                p_files(i).goods_code, 
                p_files(i).option_code, 
                p_files(i).goods_name, 
                p_files(i).option_name,
                p_files(i).goods_standard, 
                p_files(i).quantity);
    END LOOP;

    -- 4. 출고 정보 저장
    v_delivery_id := 'bsn_dlivy_' || bsn_delivery_seq.nextval;
    INSERT INTO bsn_delivery (delivery_id,
                              order_id,
                              employee_num,
                              delivery_status,
                              company_num,
                              remark)
    VALUES (v_delivery_id, 
            v_order_id, 
            p_employee_num,
            'DS01',  -- 초기 상태
            p_company_num,
            p_remark);

    -- 5. 출고 상세 정보 저장
    FOR i IN 1..p_files.COUNT LOOP

        -- 주문 상세 id 찾기
        SELECT order_detail_id 
        INTO v_order_detail_id
        FROM bsn_order_detail
        WHERE order_id = v_order_id
        AND goods_code = p_files(i).goods_code
        AND option_code = p_files(i).option_code;

        -- 상품 옵션의 바코드 찾기
        SELECT option_barcode
        INTO v_option_barcode
        FROM purchse_option
        where option_code = p_files(i).option_code;

        INSERT INTO bsn_delivery_detail (delivery_detail_id, 
                                         delivery_id, 
                                         order_detail_id,
                                         option_code,
                                         option_barcode,
                                         delivery_demand_qnt, 
                                         delivery_total_qnt,
                                         sufficiency_status,
                                         goods_standard)
        VALUES (bsn_delivery_detail_seq.nextval, 
                v_delivery_id, 
                v_order_detail_id,
                p_files(i).option_code,
                v_option_barcode,
                p_files(i).quantity,  -- 요청 수량
                0,  -- 초기 출고 수량은 0
                0,  -- 초기 상태는 부족(0)
                p_files(i).goods_standard);  
    END LOOP;

    -- 6. 주문 상태 업데이트
    UPDATE bhf_order
    SET progress_status = '승인'
    WHERE order_code = p_order_code;

        -- 7. 주문 등록 이력 추가(등록일은 주문 테이블처럼 기본값이므로 둘다 같은 값으로 등록됨)
    INSERT INTO bsn_cs_change_history (
        history_num, 
        order_id, 
        history_type, 
        history_contents
    )
    VALUES (
        bsn_cs_change_history_seq.nextval,  -- 이력 번호 시퀀스
        v_order_id, 
        'OH01',  -- 주문 등록 이력
        '지점ID: '|| p_branch_office_id ||', 주문 ID: ' || v_order_id || ', 주문명: ' || v_order_name || ' [등록됨]'
    );

EXCEPTION
    WHEN OTHERS THEN
        -- 오류 발생 시 롤백 및 오류 메시지 출력
