    IF p_warehousing_list.COUNT > 0 THEN
        FOR i IN 1..p_warehousing_list.COUNT LOOP
            UPDATE purchs_warehousing_body
            SET    warehousing_remaining_quantity = warehousing_remaining_quantity + p_warehousing_list(i).warehousing_qnt
            WHERE  goods_lot_num = p_warehousing_list(i).goods_lot_num;
        END LOOP;
    END IF;
    --------------------


    -- 반품 요청 상태 업데이트
    UPDATE bhf_returning
    SET progress_status = '승인'
    WHERE returning_code = p_returning_code;

