--------------------------------------------------------
--  DDL for Procedure PURCHS_ORDER_REGISTER
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PURCHS_ORDER_REGISTER" (
    p_employee_num       IN NUMBER,
    p_company_num        IN NUMBER,
    p_purchase_date      IN DATE,
    p_purchase_due_date  IN DATE,
    p_purchase_vat_flag  IN NUMBER,
    p_vendor_id          IN NUMBER,  
    p_files              IN purchs_order_file_Array  
)
IS
    v_today        VARCHAR2(8);  -- 오늘 날짜 (YYYYMMDD)
    v_last_seq     NUMBER;         -- 해당 날짜의 마지막 SEQUENCE 값
    v_purchase_num NUMBER;         -- 최종 발주번호
    v_total_amount NUMBER := 0;    -- 발주 총액
BEGIN
    -- 오늘 날짜 가져오기
    v_today := TO_CHAR(SYSDATE, 'YYYYMMDD');

    -- 해당 날짜의 가장 큰 발주번호의 시퀀스 부분 조회  
    -- purchase_num은 NUMBER이므로 12자리 문자열('FM000000000000')로 변환 후 SUBSTR 사용
    SELECT NVL(MAX(TO_NUMBER(SUBSTR(TO_CHAR(purchase_num, 'FM000000000000'), 9, 4))), 0)
      INTO v_last_seq
      FROM purchs_purchase_header
     WHERE TO_CHAR(purchase_date, 'YYYYMMDD') = v_today;

    -- 최종 발주번호 생성 (YYYYMMDD + SEQ)
    v_purchase_num := TO_NUMBER(v_today || LPAD(v_last_seq + 1, 4, '0'));

    -- 발주 헤드 등록
    INSERT INTO purchs_purchase_header(
          purchase_num,
          purchase_date,
          purchase_due_date,
          purchase_vat_flag,
          purchase_status,
          vendor_id,
          employee_num,
          company_num
    )
    VALUES (
          v_purchase_num,                    -- 생성된 발주번호 사용
          p_purchase_date,
          p_purchase_due_date,
          p_purchase_vat_flag,
          fn_get_cmmn_name('VS001'),
          p_vendor_id,
          p_employee_num,
          p_company_num
    );

    -- 발주 바디 추가 (상품 리스트 처리)
    IF p_files IS NOT NULL AND p_files.COUNT > 0 THEN
        FOR i IN 1 .. p_files.COUNT LOOP
            INSERT INTO purchs_purchase_body (
                  purchase_body_num,
                  purchase_quantity,
                  purchase_supply_price,
                  purchase_unit_price, 
                  purchase_vat, 
                  purchase_num,
                  option_num,
                  order_plan_body_num,
                  goods_standard,
                  company_num
            ) 
            VALUES (
                  purchs_order_body_seq.NEXTVAL,   -- 발주 바디 SEQUENCE
                  p_files(i).purchase_quantity,
                  p_files(i).purchase_supply_price,
                  p_files(i).purchase_unit_price,
                  p_files(i).purchase_vat,
                  v_purchase_num,                  -- 생성된 발주번호 사용
                  p_files(i).option_num,
                  p_files(i).order_plan_body_num,
                  p_files(i).goods_standard,
                  p_files(i).company_num
            );
        END LOOP;
    END IF;

    -- 발주 총 금액 계산 및 업데이트
    BEGIN
        SELECT SUM(purchase_supply_price + purchase_vat)
          INTO v_total_amount
          FROM purchs_purchase_body
         WHERE purchase_num = v_purchase_num
         GROUP BY purchase_num;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            v_total_amount := 0;
    END;

    UPDATE purchs_purchase_header
       SET puchase_total_amount = v_total_amount
     WHERE purchase_num = v_purchase_num;

    COMMIT;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK;
        RAISE_APPLICATION_ERROR(-20003, '예기치 않은 오류 발생: ' || SQLERRM);
END;

