--------------------------------------------------------
--  DDL for Procedure PURCHS_ORDER_UPDATE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PURCHS_ORDER_UPDATE" (
    p_purchase_num       IN NUMBER,
    p_employee_num       IN NUMBER,
    p_company_num        IN NUMBER,
    p_purchase_due_date  IN DATE,
    p_purchase_vat_flag  IN NUMBER,
    p_purchase_recorde_reason IN VARCHAR2,
    p_files              IN purchs_order_modify_file_array  
)
IS
    v_prev_body purchs_purchase_body%ROWTYPE;      --  이전 바디 데이터 저장
    v_prev_header purchs_purchase_header%ROWTYPE;  -- 헤더 데이터 저장
BEGIN
    --  헤더 정보 가져오기 (한 번만 조회)
    SELECT * INTO v_prev_header
    FROM purchs_purchase_header
    WHERE purchase_num = p_purchase_num;

    -- 바디 변경 사항 적용 (p_files 배열 처리)
    FOR i IN 1 .. p_files.COUNT LOOP
        --  기존 바디 데이터 조회
        SELECT * INTO v_prev_body
        FROM purchs_purchase_body
        WHERE purchase_body_num = p_files(i).purchase_body_num;

        -- 개별 바디 변경 기록 저장 (헤더 정보 함께 포함)
        INSERT INTO purchs_purchase_record (
            PURCHASE_RECORD_NUM, 
            PURCHASE_RECORD_DATE, 
            PURCHASE_RECORDE_REASON,
            PURCHASE_RECORD_TYPE, 
            PREV_QUANTITY, NEW_QUANTITY,
            PREV_UNIT_PRICE, NEW_UNIT_PRICE, 
            PREV_TOTAL_AMOUNT, NEW_TOTAL_AMOUNT,
            PREV_STATUS, NEW_STATUS, 
            PREV_DUE_DATE, NEW_DUE_DATE,
            PREV_VAT_FLAG, NEW_VAT_FLAG, 
            EMPLOYEE_NUM, 
            PURCHASE_NUM, 
            COMPANY_NUM
        ) VALUES (
            purchs_purchase_record_seq.NEXTVAL, 
            SYSDATE, 
            p_purchase_recorde_reason,
            'update', 
            v_prev_body.PURCHASE_QUANTITY, p_files(i).purchase_quantity,
            v_prev_body.PURCHASE_UNIT_PRICE, p_files(i).purchase_unit_price,
            v_prev_body.PURCHASE_SUPPLY_PRICE, p_files(i).purchase_supply_price,
            v_prev_header.PURCHASE_STATUS, fn_get_cmmn_name('VS001'), 
            v_prev_header.PURCHASE_DUE_DATE, p_purchase_due_date,
            v_prev_header.PURCHASE_VAT_FLAG, p_purchase_vat_flag,
            p_employee_num,
            p_purchase_num, 
            p_company_num
        );

        -- 기존 바디 데이터 업데이트
        UPDATE purchs_purchase_body
        SET purchase_quantity = p_files(i).purchase_quantity,
            purchase_unit_price = p_files(i).purchase_unit_price,
            purchase_supply_price = p_files(i).purchase_supply_price,
            purchase_vat = p_files(i).purchase_vat
        WHERE purchase_body_num = p_files(i).purchase_body_num;
    END LOOP;

    --  발주 헤더 정보 업데이트 (최종 변경 사항 적용)
    UPDATE purchs_purchase_header
    SET purchase_due_date = p_purchase_due_date,
        purchase_vat_flag = p_purchase_vat_flag,
        purchase_status = fn_get_cmmn_name('VS001'),
        employee_num = p_employee_num
    WHERE purchase_num = p_purchase_num;

    --  발주 총 금액 업데이트
    UPDATE purchs_purchase_header
    SET puchase_total_amount = (
        SELECT SUM(purchase_supply_price + purchase_vat)
        FROM purchs_purchase_body
        WHERE purchase_num = p_purchase_num
    )
    WHERE purchase_num = p_purchase_num;

    COMMIT;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK;
        RAISE_APPLICATION_ERROR(-20004, '발주 수정 중 오류 발생: ' || SQLERRM);
END purchs_order_update;

