--------------------------------------------------------
--  DDL for Procedure PURCHS_PRODUCT_REGISTER
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PURCHS_PRODUCT_REGISTER" (
    p_goods_name IN VARCHAR2,
    p_goods_cost IN NUMBER,
    p_goods_price IN NUMBER,
    p_goods_supply_price IN NUMBER,
    p_goods_standard IN VARCHAR2,
    p_goods_description IN VARCHAR2 DEFAULT NULL,  -- 기본값을 NULL로 설정
    p_goods_image IN VARCHAR2,
    p_classification_id IN NUMBER,
    p_brand_id IN NUMBER,
    p_employee_num IN NUMBER,
    p_vendor_id IN NUMBER,
    p_companyNum IN NUMBER,
    p_files IN purchsfileArray  -- ✅ `IN` 추가 (필수!)
)
IS
    v_brand_code VARCHAR2(1000);
    v_classification_code VARCHAR2(1000);
    v_goods_num NUMBER;
    v_goods_code VARCHAR2(1000);
    v_goods_description VARCHAR2(4000);  -- ✅ goods_description을 위한 지역 변수 선언

    -- ✅ 예외처리용 커스텀 예외 선언
    e_brand_not_found EXCEPTION;
    e_classification_not_found EXCEPTION;

BEGIN
    -- ✅ `p_goods_description`이 빈 문자열("")이라면 NULL로 변환
    v_goods_description := CASE WHEN p_goods_description IS NULL OR TRIM(p_goods_description) = '' THEN NULL ELSE p_goods_description END;

    -- ✅ 브랜드 코드 가져오기 (값이 없으면 예외 발생)
    BEGIN
        SELECT brand_code
        INTO v_brand_code
        FROM stdr_brand
        WHERE brand_id = p_brand_id;

        IF v_brand_code IS NULL THEN
            RAISE e_brand_not_found;
        END IF;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            RAISE_APPLICATION_ERROR(-20001, '해당 브랜드가 존재하지 않습니다.');
    END;

    -- ✅ 분류 코드 가져오기 (값이 없으면 예외 발생)
    BEGIN
        SELECT classification_code
        INTO v_classification_code
        FROM stdr_classification
        WHERE classification_id = p_classification_id;

        IF v_classification_code IS NULL THEN
            RAISE e_classification_not_found;
        END IF;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            RAISE_APPLICATION_ERROR(-20002, '해당 분류가 존재하지 않습니다.');
    END;

    -- ✅ 상품 등록
    INSERT INTO purchse_goods (
        goods_num, goods_name, goods_cost, goods_code, goods_price, goods_supply_price,
        goods_standard, goods_description, goods_image, classification_id, brand_id, employee_num, vendor_id, company_num
    ) VALUES (
        purchse_goods_seq.NEXTVAL, 
        p_goods_name,
        p_goods_cost,
        v_classification_code || v_brand_code || purchse_goods_code_seq.NEXTVAL,
        p_goods_price, 
        p_goods_supply_price,
        p_goods_standard,
        v_goods_description,  -- ✅ 변환된 NULL 허용 변수 사용
        p_goods_image,
        p_classification_id,
        p_brand_id,
        p_employee_num,
        p_vendor_id,
        p_companyNum
    );

    -- ✅ 가장 최근에 등록된 `goods_num` 가져오기
    SELECT goods_num, goods_code
    INTO v_goods_num, v_goods_code
    FROM purchse_goods
    WHERE ROWNUM = 1
    ORDER BY goods_num DESC;

    -- ✅ 옵션 정보 추가 (p_files가 NULL이 아니고 데이터가 있을 때만)
    IF p_files IS NOT NULL AND p_files.COUNT > 0 THEN
        FOR i IN 1..p_files.COUNT LOOP
            INSERT INTO purchse_option (
                option_num, option_code, option_name, warehouse_id, goods_num, company_num, option_barcode
            ) VALUES (
                purchse_option_seq.NEXTVAL,
                v_goods_code || purchse_option_code_seq.NEXTVAL,
                p_files(i).option_name,
                p_files(i).warehouse_id,
                v_goods_num,
                p_companyNum,
                p_vendor_id || v_goods_num || purchse_option_seq.NEXTVAL || '000000'
            );
        END LOOP;
    END IF;

EXCEPTION
    WHEN OTHERS THEN
        RAISE_APPLICATION_ERROR(-20003, '예기치 않은 오류 발생: ' || SQLERRM);
END;

/
--------------------------------------------------------
--  DDL for Procedure PURCHS_PRODUCT_UPDATE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PURCHS_PRODUCT_UPDATE" (
    p_goods_num IN NUMBER,  -- 수정할 상품의 PK
    p_goods_name IN VARCHAR2,
    p_goods_cost IN NUMBER,
    p_goods_price IN NUMBER,
    p_goods_supply_price IN NUMBER,
    p_goods_standard IN VARCHAR2,
    p_goods_description IN VARCHAR2 DEFAULT NULL,  
    p_goods_image IN VARCHAR2 DEFAULT NULL, -- ✅ NULL 허용
    p_employee_num IN NUMBER,
    p_companyNum IN NUMBER,
    p_files IN PURCHS_MODIFY_FILE_ARRAY  -- 옵션 목록 배열
)
IS
    v_goods_description VARCHAR2(4000);
    v_goods_code VARCHAR2(1000); -- ✅ 상품 코드 변수 추가
    v_vendor_id NUMBER; -- ✅ 상품의 vendor_id 저장할 변수 추가

    -- ✅ 예외처리용 커스텀 예외 선언
    e_goods_not_found EXCEPTION;

BEGIN
    -- ✅ 상품 설명이 비어있다면 NULL 처리
    v_goods_description := CASE 
        WHEN p_goods_description IS NULL OR TRIM(p_goods_description) = '' 
        THEN NULL 
        ELSE p_goods_description 
    END;

    -- ✅ 상품 코드 및 vendor_id 가져오기 (option_code, option_barcode 생성 시 사용)
    BEGIN
        SELECT goods_code, vendor_id 
        INTO v_goods_code, v_vendor_id
        FROM purchse_goods
        WHERE goods_num = p_goods_num;

        -- ✅ 조회된 값이 없으면 예외 발생
        IF v_goods_code IS NULL OR v_vendor_id IS NULL THEN
            RAISE e_goods_not_found;
        END IF;

    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            RAISE_APPLICATION_ERROR(-20005, '❌ 해당 상품을 찾을 수 없습니다. (상품번호: ' || p_goods_num || ')');
        WHEN OTHERS THEN
            RAISE_APPLICATION_ERROR(-20006, '❌ 상품 조회 중 오류 발생: ' || SQLERRM);
    END;

    -- ✅ 상품 정보 업데이트
    UPDATE purchse_goods
    SET goods_name = p_goods_name,
        goods_cost = p_goods_cost,
        goods_price = p_goods_price,
        goods_supply_price = p_goods_supply_price,
        goods_standard = p_goods_standard,
        goods_description = v_goods_description,
        goods_image = COALESCE(p_goods_image, goods_image), -- ✅ `NULL`이면 기존 값 유지
        employee_num = p_employee_num
    WHERE goods_num = p_goods_num;

    -- ✅ 옵션 정보 처리 (MERGE INTO 활용)
    IF p_files IS NOT NULL AND p_files.COUNT > 0 THEN
        FOR i IN 1..p_files.COUNT LOOP
            MERGE INTO purchse_option o
            USING (SELECT p_files(i).option_num AS option_num FROM DUAL) src
            ON (o.option_num = src.option_num)
            WHEN MATCHED THEN
                -- ✅ 기존 옵션이 존재하면 변경된 경우에만 업데이트
                UPDATE SET 
                    option_name = p_files(i).option_name,
                    option_safety_invoice = NVL(p_files(i).option_safety_invoice, 0), -- 안전재고 기본값 0
                    warehouse_id = p_files(i).warehouse_id,
                    option_use_flag = p_files(i).option_use_flag
            WHEN NOT MATCHED THEN
                -- ✅ 기존 옵션이 없으면 새로운 옵션 추가
                INSERT (
                    option_num, option_code, option_name, warehouse_id, goods_num, company_num, 
                    option_use_flag, option_safety_invoice, option_barcode  -- ✅ option_barcode 추가
                ) VALUES (
                    purchse_option_seq.NEXTVAL,
                    v_goods_code || purchse_option_code_seq.NEXTVAL,  -- ✅ 일관된 option_code 생성 방식
                    p_files(i).option_name,
                    p_files(i).warehouse_id,
                    p_goods_num,
                    p_companyNum,
                    p_files(i).option_use_flag,
                    NVL(p_files(i).option_safety_invoice, 0), -- ✅ 안전재고 기본값 0 설정
                    v_vendor_id || p_goods_num || purchse_option_seq.NEXTVAL || '000000' -- ✅ option_barcode 생성
                );
        END LOOP;
    END IF;

    COMMIT;  -- ✅ 변경 사항 저장

EXCEPTION
    WHEN e_goods_not_found THEN
        ROLLBACK;
        RAISE;
    WHEN OTHERS THEN
        ROLLBACK;  -- 오류 발생 시 롤백
        RAISE_APPLICATION_ERROR(-20007, '❌ 상품 수정 중 오류 발생: ' || SQLERRM);
