--------------------------------------------------------
--  DDL for Procedure PURCHS_WAREHOUSE_REGISTER
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PURCHS_WAREHOUSE_REGISTER" (
    p_employee_num       IN NUMBER,
    p_company_num        IN NUMBER,
    p_vendor_id          IN NUMBER,  
    p_files              IN purchs_warehouse_file_Array  
)
IS
    v_today                  VARCHAR2(6);  -- 'yyMMdd' 형식 저장
    v_last_seq               NUMBER := 0;  
    v_warehouse_num          NUMBER;       
    v_total_amount           NUMBER := 0;  
    v_warehousing_header_num NUMBER;
    v_option_code            VARCHAR2(20);
    v_distribution_time_limit NUMBER := 0;
    v_goods_standard_num     NUMBER := 1;  
    v_lot_seq                NUMBER := 0;  
    v_goods_lot_num          VARCHAR2(50);
    v_goods_consumption_date DATE;
    v_total_purchased_qty    NUMBER := 0;  -- ✅ 총 발주 수량
    v_total_received_qty     NUMBER := 0;  -- ✅ 총 입고 수량
BEGIN
    v_today := TO_CHAR(SYSDATE, 'yyMMdd');  -- ✅ 'yyMMdd' 형식으로 저장

    -- ✅ 오늘 날짜의 가장 큰 입고번호(warehousing_chit) 조회
    BEGIN
        SELECT NVL(MAX(TO_NUMBER(SUBSTR(warehousing_chit, -4))), 0)
          INTO v_last_seq
          FROM purchs_warehousing_header
         WHERE TO_CHAR(warehousing_date, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD');
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            v_last_seq := 0;
    END;

    -- ✅ 최종 입고번호 생성
    v_warehouse_num := TO_NUMBER(v_today || LPAD(v_last_seq + 1, 4, '0'));

    -- ✅ 입고 헤더 등록
    INSERT INTO purchs_warehousing_header (
        warehousing_header_num, 
        warehousing_date, 
        employee_num, 
        warehousing_chit,  
        vendor_id, 
        company_num
    ) VALUES (
        purchs_warehouse_head_seq.NEXTVAL, 
        SYSDATE, 
        p_employee_num, 
        v_warehouse_num, 
        p_vendor_id, 
        p_company_num
    ) RETURNING warehousing_header_num INTO v_warehousing_header_num;

    -- ✅ 입고 바디 추가
    IF p_files IS NOT NULL AND p_files.COUNT > 0 THEN
        FOR i IN 1 .. p_files.COUNT LOOP
            BEGIN
                -- ✅ 옵션 정보 및 유통기한 개월 수 조회
                SELECT o.option_code, s.distribution_time_limit 
                  INTO v_option_code, v_distribution_time_limit
                  FROM purchse_option o  
                  JOIN purchse_goods g ON o.goods_num = g.goods_num
                  JOIN stdr_classification s ON g.classification_id = s.classification_id
                 WHERE o.option_num = p_files(i).option_num;
            EXCEPTION
                WHEN NO_DATA_FOUND THEN
                    v_option_code := 'UNKNOWN';
                    v_distribution_time_limit := 0;
            END;

            -- ✅ 유통기한(소비기한) 계산
            v_goods_consumption_date := ADD_MONTHS(p_files(i).manufacture_date, v_distribution_time_limit * 12);

            -- ✅ 옵션별 LOT SEQ 조회 (유통기한 단위로 증가)
            BEGIN
                SELECT NVL(MAX(TO_NUMBER(SUBSTR(goods_lot_num, -3))), 0)
                  INTO v_lot_seq
                  FROM purchs_warehousing_body
                 WHERE TO_CHAR(goods_consumption_date, 'YYYYMMDD') = TO_CHAR(v_goods_consumption_date, 'YYYYMMDD')
                   AND option_num = p_files(i).option_num;
            EXCEPTION
                WHEN NO_DATA_FOUND THEN
                    v_lot_seq := 0;
            END;

            -- ✅ LOT SEQ 증가 및 LOT 번호 생성 (yyMMdd 형식 적용)
            v_lot_seq := v_lot_seq + 1;
            v_goods_lot_num := v_option_code || v_today || TO_CHAR(v_goods_consumption_date, 'yyMMdd') || LPAD(v_lot_seq, 3, '0');

            -- ✅ 규격(goods_standard)에서 개당 갯수 추출
            BEGIN
                SELECT NVL(TO_NUMBER(REGEXP_SUBSTR(p_files(i).goods_standard, '\d+', 1, 2)), 1)
                INTO v_goods_standard_num
                FROM DUAL;
            EXCEPTION
                WHEN OTHERS THEN
                    v_goods_standard_num := 1;  -- 숫자 추출 실패 시 기본값 1
            END;

            -- ✅ 입고 바디 데이터 삽입 (개당 갯수 반영)
            INSERT INTO purchs_warehousing_body (
                warehousing_body_num,
                goods_lot_num, 
                goods_consumption_date,
                warehousing_standard_quantity, 
                warehousing_total_quantity,
                warehousing_remaining_quantity,
                warehousing_unit_total_amount,
                warehousing_unit_price, 
                warehousing_supply_price, 
                warehousing_vat,
                warehouse_id, 
                warehousing_header_num, 
                purchase_body_num, 
                company_num, 
                option_num,
                manufacture_date
            ) VALUES (
                purchs_warehouse_body_seq.NEXTVAL, 
                v_goods_lot_num, 
                v_goods_consumption_date,
                p_files(i).warehousing_standard_quantity,
                p_files(i).warehousing_standard_quantity * v_goods_standard_num,  -- ✅ 개당 갯수 반영
                p_files(i).warehousing_standard_quantity * v_goods_standard_num,  -- ✅ 개당 갯수 반영
                p_files(i).warehousing_supply_price + p_files(i).warehousing_vat,  
                p_files(i).warehousing_unit_price, 
                p_files(i).warehousing_supply_price, 
                p_files(i).warehousing_vat, 
                p_files(i).warehouse_id,
                v_warehousing_header_num, 
                p_files(i).purchase_body_num, 
                p_company_num, 
                p_files(i).option_num, 
                p_files(i).manufacture_date
            );

            -- ✅ 발주 상태 업데이트 (PURCHASE_BODY_NUM이 있는 경우)
            IF p_files(i).purchase_body_num IS NOT NULL THEN
                -- 🔹 발주 수량과 입고 수량 비교
                SELECT SUM(purchase_quantity), SUM(warehousing_total_quantity)
                  INTO v_total_purchased_qty, v_total_received_qty
                  FROM purchs_purchase_body pb
                  LEFT JOIN purchs_warehousing_body wb 
                    ON pb.purchase_body_num = wb.purchase_body_num
                 WHERE pb.purchase_body_num = p_files(i).purchase_body_num
                 GROUP BY pb.purchase_body_num;

                -- 🔹 입고 수량이 발주 수량 이상이면 발주 상태 변경
                IF v_total_received_qty >= v_total_purchased_qty THEN
                    UPDATE purchs_purchase_header
                       SET purchase_status = fn_get_cmmn_name('VS002')
                     WHERE purchase_num = (SELECT purchase_num FROM purchs_purchase_body WHERE purchase_body_num = p_files(i).purchase_body_num);
                END IF;
            END IF;

            -- ✅ 총 입고 금액 갱신
            v_total_amount := v_total_amount + 
                              ((p_files(i).warehousing_standard_quantity * p_files(i).warehousing_supply_price) + p_files(i).warehousing_vat);
        END LOOP;
    END IF;

    -- ✅ 입고 헤더의 총 금액 업데이트
    UPDATE purchs_warehousing_header
       SET warehousing_total_amount = v_total_amount
     WHERE warehousing_header_num = v_warehousing_header_num;

    COMMIT;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK;
        RAISE_APPLICATION_ERROR(-20003, '예기치 않은 오류 발생: ' || SQLERRM);
END;

