--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "FN_GENERATE_EMPLOYEE_ID" RETURN VARCHAR2 IS
    v_max_id VARCHAR2(20);
    v_new_seq NUMBER;
    v_new_id VARCHAR2(20);
    v_today VARCHAR2(6);
BEGIN
    -- 오늘 날짜 YYMMDD 가져오기
    v_today := TO_CHAR(SYSDATE, 'YYMMDD');

    -- 가장 최근 사원 ID 가져오기 (오늘 날짜 기준)
    SELECT NVL(MAX(employee_id), '000000000')
    INTO v_max_id
    FROM HR_EMPLOYEE
    WHERE SUBSTR(employee_id, 1, 6) = v_today;

    -- 기존 데이터가 없을 경우, 기본값 설정
    IF v_max_id = '000000000' THEN
        v_new_seq := 1; -- 데이터가 없으면 001부터 시작
    ELSE
        v_new_seq := TO_NUMBER(SUBSTR(v_max_id, 7, 3)) + 1;
    END IF;

    -- 새 사원번호 생성 (YYMMDD + 001 형식)
    v_new_id := v_today || LPAD(v_new_seq, 3, '0');

    RETURN v_new_id;
END;

